drop table if exists cma_tipo_processo;

-- para requerimentos
create table cma_tipo_processo (
  id_tipo_processo varchar(64),
  id_tipo_processo_num integer,
  grupo varchar(60),
  codigo_subgrupo varchar(64),
  subgrupo varchar(60),
  id_tipo_requerimento integer,
  pasta_processo_base varchar(200),
  tipo_processo_macro char(1),
  perfil_responsavel varchar(100),
  descricao varchar(200)
);



